mapper.insert:
        INSERT INTO USER (
	          USER_ID
	        , USER_PASSWORD
	        , USER_NAME
	        , USER_EMAIL
	        , USER_PHONE
	        , DEPARTMENT_ID
	        , JOB_POSITION_ID
	        , USER_JOIN_DATE
	        , BANK_ID
	        , USER_ACCOUNT
	        , USER_ADDRESS
        )
        VALUES 

        (
	          #{user.user_id}
	        , #{user.user_password}
	        , #{user.user_name}
			, #{user.user_email}
			, #{user.user_phone}
			, #{user.department_id}
			, #{user.job_position_id}
			, #{user.user_join_date}
			, #{user.bank_id}
			, #{user.user_account}
			, #{user.user_address}
        )

mapper.select:
SELECT USER_NAME, USER_ID, DEPARTMENT_ID, JOB_POSITION_ID, USER_PHONE, USER_STATE,
DATE_FORMAT(USER_JOIN_DATE, '%Y.%m.%d') AS USER_JOIN_DATE
FROM USER 
WHERE USER_ID != 'SYSTEM' 

    AND USER_STATE = #{user_state}


    AND DEPARTMENT_ID = #{department_id}


    AND JOB_POSITION_ID = #{job_position_id}


    AND USER_JOIN_DATE BETWEEN #{first_date} AND #{second_date}



        AND USER_ID LIKE concat('%', #{search_value}, '%')


        AND USER_NAME LIKE concat('%', #{search_value}, '%')


order by USER_ID DESC 
limit #{start}, #{perPage}

SELECT count(*) as total 
FROM USER 
WHERE USER_ID != 'SYSTEM' 

    AND USER_STATE = #{user_state}


    AND DEPARTMENT_ID = #{department_id}


    AND JOB_POSITION_ID = #{job_position_id}


    AND USER_JOIN_DATE BETWEEN #{first_date} AND #{second_date}



        AND USER_ID LIKE concat('%', #{search_value}, '%')


        AND USER_NAME LIKE concat('%', #{search_value}, '%')
